import {NavLink, withRouter} from "react-router-dom";
import React, {Component} from 'react'
import Nav from "../../components/Nav";
import Super from "../../super"
import {Accordion, Icon, List, Toast, Popover} from "antd-mobile";
import Storage from "../../units/storage";
import "index.less"
const Item = Popover.Item;

class TestHome extends Component{
    state={homeState:[]}

    componentDidMount() {
        this.request()
    }
    request = () => {
        Super.super({
            url: 'api2/meta/menu/get_blocks',
        }).then((res) => {
            let currentBlockId=res.currentBlockId;
            console.log(res);
            this.setBlocks(currentBlockId,res.blocks);
        })
    }
    setBlocks=(blockId,blocks)=>{
        Toast.info(blockId);
        const menuTreeNode=this.renderMenu(blocks);
        console.log(menuTreeNode.length);
        this.setState({menuTreeNode});
    }
    renderMenu=(data)=>{
        return data.map((item)=>{
            return <div>bbb</div>
        });
    }

    handlePop = (value) => {
        this.props.history.push(`/${value}`)
    }

    render() {
        const {homeDate,menuTreeNode}=this.state
        return(
            <div>
                <Nav
                title="aaa"
                data={homeDate}
                level={1}
                />
                <div style={{marginTop:'50px'}}>{menuTreeNode}</div>
            </div>
        );
    }
}

export default withRouter(TestHome)
